let sonido = new Audio( "data:audio/wav;base64,");  
let refe = document.querySelector("#refe");
let submitButton = document.getElementById("myButton")
let form = document.getElementById("myForm")

form.addEventListener('submit', function() {

    // Disable the submit button
    submitButton.setAttribute('disabled', 'disabled');
 
    // Change the "Submit" text
    submitButton.value = 'Please wait...';
             
 }, false);

Quagga.init({
    inputStream : {
      name : "Live",
      type : "LiveStream",
      target: document.querySelector('#camara')    // Or '#yourElement' (optional)
    },
    frecuency : 30,
    decoder : {
      readers :  ["code_128_reader","ean_reader","ean_8_reader","code_39_reader","code_39_vin_reader","codabar_reader","upc_reader","upc_e_reader","i2of5_reader","2of5_reader","code_93_reader"],
    }
  }, function(err) {
      if (err) {
          console.log(err);
          return
      }
      console.log("Initialization finished. Ready to start");
      Quagga.start();
  });

  Quagga.onProcessed(function(result){
    var drawingCtx = Quagga.canvas.ctx.overlay,
     drawingCanvas = Quagga.canvas.dom.overlay;
    drawingCanvas.style.display = 'none';

    if(result){
        if(result.boxes){
            drawingCtx.clearRect(0,0,parseInt(drawingCanvas.getAttribute("width")),parseInt(drawingCanvas.getAttribute("height")));
            result.boxes.filter(function(box){
                return box !== result.box;
            }).forEach(function(box){
                Quagga.ImageDebug.drawPath(box,{
                    x:0,
                    y:1
                }, drawingCtx,{
                    color:"green",
                    lineWidth: 2
                });
            }); 
        }

        if (result.box){
            Quagga.ImageDebug.drawPath(result.box,{
                x:0,
                y:1
            },drawingCtx,{
                color: "#00F",
                lineWidth: 2
            });
        }
        if(result.codeResult && result.codeResult.code){
            Quagga.ImageDebug.drawPath(result.line,{
                x:'x',
                y:'y'
            }, drawingCtx,{
                color: 'red',
                lineWidth:3
            });
        }
    }
  });
  Quagga.onDetected(function(data){
    sonido.play()
    refe.value=data.codeResult.code;
    submitButton.click();
  });